-- Move/copy the by the user saved map templates to the new table analysis.user_map_templates
DO $$
DECLARE _userid character varying(50);
DECLARE _workspaceid bigint;
BEGIN
	FOR _userid IN
		SELECT DISTINCT userid FROM analysis.map_templates
	LOOP
		RAISE NOTICE 'USER: % ', _userid;

		SELECT workspaceid INTO _workspaceid FROM analysis.user_workspaces WHERE userid = _userid AND isdefault = TRUE;
		IF NOT FOUND THEN
			INSERT INTO analysis.user_workspaces (userid, workspacename, isdefault) VALUES ( _userid, 'default', TRUE);
		END IF;

		SELECT workspaceid INTO _workspaceid FROM analysis.user_workspaces WHERE userid = _userid AND isdefault = TRUE;

		INSERT INTO analysis.user_map_templates
		  (
		  userid,
		  workspaceid,
		  -- map_tpl_id,
		  map_tpl_name,
		  istemplate,
		  mapviewposition,
		  mapviewsize,
		  productcode,
		  subproductcode,
		  productversion,
		  mapsetcode,
		  legendid,
		  legendlayout,
		  legendobjposition,
		  showlegend,
		  titleobjposition,
		  titleobjcontent,
		  disclaimerobjposition,
		  disclaimerobjcontent,
		  logosobjposition,
		  logosobjcontent ,
		  showobjects,
		  showtoolbar,
		  showgraticule,
		  scalelineobjposition,
		  vectorlayers,
		  outmask,
		  outmaskfeature,
		  auto_open,
		  zoomextent,
		  mapsize,
		  mapcenter,
		  -- parent_tpl_id,
		  showtimeline
		  )
		SELECT  userid,
			_workspaceid as workspaceid,
			-- map_tpl_id,
			templatename as map_tpl_name,
			TRUE as istemplate,
			mapviewposition,
			mapviewsize,
			productcode,
			subproductcode,
			productversion,
			mapsetcode,
			legendid,
			legendlayout,
			legendobjposition,
			showlegend,
			titleobjposition,
			titleobjcontent,
			disclaimerobjposition,
			disclaimerobjcontent,
			logosobjposition,
			logosobjcontent ,
			showobjects,
			TRUE as showtoolbar,
			FALSE as showgraticule,
			scalelineobjposition,
			vectorlayers,
			outmask,
			outmaskfeature,
			auto_open,
			zoomextent,
			mapsize,
			mapcenter,
			-- parent_tpl_id,
			TRUE as showtimeline
		FROM analysis.map_templates
		WHERE userid = _userid;


		UPDATE analysis.user_map_templates
		SET parent_tpl_id = map_tpl_id
		WHERE userid = _userid
		AND workspaceid = _workspaceid;

	END LOOP;
END $$;


