-- Move/copy the by the user saved map templates to the new table analysis.user_map_templates
DO $$
DECLARE _userid character varying(50);
DECLARE _workspaceid bigint;
BEGIN
	FOR _userid IN
		SELECT DISTINCT userid FROM analysis.map_templates
	LOOP
		RAISE NOTICE 'USER: % ', _userid;

		SELECT workspaceid INTO _workspaceid FROM analysis.user_workspaces WHERE userid = _userid AND isdefault = TRUE;
		IF NOT FOUND THEN
			INSERT INTO analysis.user_workspaces (userid, workspacename, isdefault) VALUES ( _userid, 'default', TRUE);
		END IF;

		SELECT workspaceid INTO _workspaceid FROM analysis.user_workspaces WHERE userid = _userid AND isdefault = TRUE;

		INSERT INTO analysis.user_map_templates
		  (
		  userid,
